import hashlib


def sha256_to_int(input_string):
    # 计算字符串的 SHA-256 哈希值
    sha256_hash = hashlib.sha256(input_string.encode()).hexdigest()

    # 将十六进制哈希值转换为整数
    hash_int = int(sha256_hash, 16)

    return hash_int


